import { Component,OnInit,Inject } from '@angular/core';
import{FormBuilder,FormGroup,Validators} from "@angular/forms";
import {MatDialogRef,MAT_DIALOG_DATA} from '@angular/material/dialog';
import {MatSnackBar} from '@angular/material/snack-bar';
import{MAT_DATE_FORMATS} from '@angular/material/core';
import * as moment from 'moment';

import { Branch } from 'src/app/Interfaces/branch';
import { BranchService } from 'src/app/Services/branch.service';
import { Currency } from 'src/app/Interfaces/currency';
import { CurrencyService } from 'src/app/Services/currency.service';



export const MY_DATE_FORMATS={
  parse:{
    dateInput: 'DD/MM/YYYY',
  },
  display:{
    dateInput:'DD/MM/YYYY',
    monthYearLabel:'MMMM YYYY',
    dateA11yLabel:'LL',
    monthYearA11yLabel:'MMMM YYYY'

  }
}

@Component({
  selector: 'app-dialog-add-edit',
  templateUrl: './dialog-add-edit.component.html',
  styleUrls: ['./dialog-add-edit.component.css'],
  providers:[
    {
      provide:MAT_DATE_FORMATS,useValue:MY_DATE_FORMATS
    }
  ]
})
export class DialogAddEditComponent implements OnInit {
  formBranch:FormGroup;
  title:string = "Nuevo";
  actionButton:string ="Guardar";
  lstCurrency:Currency[]=[];

  constructor(
    private dialogReference:MatDialogRef<DialogAddEditComponent>,
    private fb: FormBuilder,
    private _snackBar: MatSnackBar,
    private _branchService:BranchService,
    private _currencyService:CurrencyService,
    @Inject(MAT_DIALOG_DATA) public dataBranch:Branch
  ){
    this.formBranch = this.fb.group({
      branchCode:["",Validators.required], 
      branchDescription:["",Validators.required], 
      branchAddress:["",Validators.required], 
      branchId:["",Validators.required], 
      branchDateCreation:["",Validators.required],
      idCurrency:["",Validators.required]
    })
    this._currencyService.getList().subscribe({
      next:(data)=>{
        this.lstCurrency=data;
      },error:(e)=>{}
  })


  }

  showAlert(message: string, action: string) {
    this._snackBar.open(message, action,{
      horizontalPosition:"end",
      verticalPosition:"top",
      duration:3000
    });
  }

  addEditBranch(){
    const modelo:Branch={
      IdBranch:0,
      BranchCode: this.formBranch.value.branchCode,
      BranchDescription: this.formBranch.value.branchDescription,
      BranchAddress:this.formBranch.value.branchAddress,
      BranchId:this.formBranch.value.branchId,
      BranchDateCreation:moment(this.formBranch.value.branchDateCreation).format("DD/MM/YYYY"),
      IdCurrency:this.formBranch.value.idCurrency,
      CurrencyName:""
    }
    if(this.dataBranch == null){
      this._branchService.add(modelo).subscribe({
        next:(data)=>{
          this.showAlert("Sucursal Creada","Listo");
          this.dialogReference.close("creado");
        },error:(e)=>{
          this.showAlert("Error al crea la sucursal","Error");
        }
      })
    }
    else{
      this._branchService.update(this.dataBranch.IdBranch,modelo).subscribe({
        next:(data)=>{
          this.showAlert("Sucursal Actualizada","Listo");
          this.dialogReference.close("actualizado");
        },error:(e)=>{
          this.showAlert("Error al creal la sucursal","Error");
        }
      })
    }
  }

  ngOnInit():void{
    if(this.dataBranch){
      this.formBranch.patchValue(
        {
          idBranch:this.dataBranch.IdBranch,
          branchCode:this.dataBranch.BranchCode,
          branchDescription:this.dataBranch.BranchDescription,
          branchAddress:this.dataBranch.BranchAddress,
          branchId:this.dataBranch.BranchId,
          branchDateCreation:moment(this.dataBranch.BranchDateCreation).format("DD/MM/YYYY"),
          idCurrency:this.dataBranch.IdCurrency,
          CurrencyName:this.dataBranch.CurrencyName
        }
      )
      this.title = "Editar";
      this.actionButton = "Actualizar"
    }

  }
}
<h1 mat-dialog-title>{{title}} Sucursal</h1>
<div mat-dialog-content>
    <form [formGroup]="formBranch">
        <mat-grid-list cols="1" rowHeight="80px">
            <mat-grid-tile>
                <mat-form-field appearance="fill">
                    <mat-label>Código</mat-label>
                    <input matInput formControlName="branchCode" autocomplete="off">
                  </mat-form-field>
            </mat-grid-tile>
            <mat-grid-tile>
                <mat-form-field appearance="fill">
                    <mat-label>Nombre</mat-label>
                    <input matInput formControlName="branchDescription" autocomplete="off">
                  </mat-form-field>
            </mat-grid-tile>
            <mat-grid-tile>
                <mat-form-field appearance="fill">
                    <mat-label>Dirección</mat-label>
                    <input matInput formControlName="branchAddress" autocomplete="off">
                  </mat-form-field>
            </mat-grid-tile>
            <mat-grid-tile>
                <mat-form-field appearance="fill">
                    <mat-label>Identificación</mat-label>
                    <input matInput formControlName="branchId" autocomplete="off">
                  </mat-form-field>
            </mat-grid-tile>
            <mat-grid-tile>
                <mat-form-field appearance="fill">
                    <mat-label>Fecha de Creación</mat-label>
                    <input matInput [matDatepicker]="picker" formControlName="branchDateCreation" autocomplete="off">
                    <mat-datepicker-toggle matIconSuffix [for]="picker"></mat-datepicker-toggle>
                    <mat-datepicker #picker></mat-datepicker>
                  </mat-form-field>





            </mat-grid-tile>
            <mat-grid-tile>
                <mat-form-field appearance="fill">
                    <mat-label>Moneda</mat-label>
                    <mat-select formControlName="idCurrency">
                        <mat-option *ngFor="let item of lstCurrency" [value]="item.IdCurrency">
                            {{item.CurrencyName}}
                        </mat-option>
                    </mat-select>


                 </mat-form-field>
            </mat-grid-tile>

          </mat-grid-list>

    </form>
</div>
<div mat-dialog-actions>
  <button mat-raised-button mat-dialog-close color="warm">Volver</button>
  <button mat-raised-button color="primary" (click)="addEditBranch()" [disabled]="formBranch.invalid">{{actionButton}}</button>
</div>
